import React, { useState } from 'react';

function Form()
{
    const [fullname,setFullName] = useState({
        fname:"",
        lname:"",
        email:"",
        password:"",
    });
    

    function Change(event){

        setFullName((preVal)=>{
            
            if(event.target.name==='fname')
            {
                return {
                    lname:preVal.lname,
                    fname:event.target.value,
                    email:preVal.email,
                    password:preVal.password,
                }
                
            }
            else if(event.target.name==='lname')
            {
                return {
                    lname:event.target.value,
                    fname:preVal.fname,
                    email:preVal.email,
                    password:preVal.password,
                }
            }
            else if(event.target.name==='email')
            {
                return {
                    lname:preVal.lname,
                    fname:preVal.fname,
                    email:event.target.value,
                    password:preVal.fname,
                }
            }
            else if(event.target.name==='password')
            {
                return {
                    
                    lname:preVal.lname,
                    fname:preVal.fname,
                    email:preVal.email,
                    password:event.target.value,                }
            }
        })
        
        }
       
    

function onSubmit(event){
    event.preventDefault();

}

    return(
        <>
        <div className="div_form">
            <form onSubmit={onSubmit}>
                  <h1>{fullname.fname} {fullname.lname} {fullname.email} {fullname.password}</h1>
                    <input type="text" placeholder="Enter Your Name" name="fname" value={fullname.fname} onChange={Change}/>
                    <br/><input type="text" placeholder="Enter Your Last Name" name="lname" value={fullname.lname}  onChange={Change}/>
                    <br/><input type="text" placeholder="Enter Your Last Name" name="email" value={fullname.email}  onChange={Change}/>
                    <br/><input type="text" placeholder="Enter Your Last Name" name="password" value={fullname.password}  onChange={Change}/>
                    <br/><button > Click </button>
                
         </form>
        </div>
        </>
    )
}

export default Form;
