import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'
const img1 = "https://picsum.photos/200/300";
const img2 = "https://picsum.photos/250/300";
const img3 = "https://picsum.photos/300/300";
const img4 = "https://picsum.photos/350/300";

let currDate = new Date();
currDate = currDate.getHours();
let greetings = "";
const cssStyle={};
if(currDate>=1 && currDate<12)
{
    greetings="Good Morning";
    cssStyle.color="Green";
}
else if(currDate>11 && currDate<=19){
    greetings = "Good Afternoon"
    cssStyle.color="Orange";
}

else if(currDate>19 && currDate<=24){

    greetings = "Good Night"
    cssStyle.color="Black";
}



ReactDOM.render(<>

    <h1 className="heading">Image Gallery</h1>
    <div className="img_div">
    <img src={img1} alt="Random"/>
    <img src={img2} alt="Random"/>
    <img src={img3} alt="Random"/>
    <img src={img4} alt="Random"/>
    </div>

    <div>

    <h1 className="greeting">Hello Sir, <span style={cssStyle}>{greetings}</span></h1>

    </div>

</>,


document.getElementById('root'));
