import React, { useState } from "react";
import List from './List';





function Todo() {
    const [item,setItem] = useState("");
    const [subItem , setSubItem] = useState([]);

    function change(event)
    {
           setItem(event.target.value);
    }

   function click(){
        setSubItem((preItem) =>
            [...preItem,item]
        )
        setItem("");
   }


   function del(id) {
    
    setSubItem((pre) => {
        return pre.filter((arr,index) =>{
            console.log(parseInt(id.target.value*2)+" H "+(index));
            return index!=parseInt(id.target.value); 
        })
    })

    }

 
return(
    <>
    <input type ="text" onChange={change} value={item}>
    </input>
    <button onClick={click}>add</button>
    <ol>
        {
            subItem.map((data,index) => {
                   return <List key={index}
                       onSelect = {del}
                       Data = {data}
                       id={index}
                   />
            } )
        }
    </ol>
    </>
);
}
export default Todo;
