import React, { useState } from "react";
import List from './List';





function Todo() {
    const [item,setItem] = useState("");
    const [subItem , setSubItem] = useState([]);
    function change(event)
    {
           setItem(event.target.value);
    }

   function click(){
        setSubItem((preItem) =>
            [...preItem,item]
        )
        setItem("");
   }


   function del(id) {
    const arr = ["z","e","s"];
    
    setSubItem( (previousitem) => {
        previousitem.filter( (arr,index) =>{
            return index!==id.target.value
        } )
    } )

    console.log(setSubItem);

}

 
return(
    <>
    <input type ="text" onChange={change} value={item}>
    </input>
    <button onClick={click}>add</button>
    <ol>
        {
            subItem.map((data) => {
                   return <List key=""
                       Data={data}
                       onSelect = {del}
                   />
            } )
        }
    </ol>
    </>
);
}
export default Todo;
