import React, { useState } from 'react';

function Form()
{
    const [fullname,setFullName] = useState({
        fname:"",
        lname:""
    });
    

    function Change(event){

        setFullName((preVal)=>{
            
            if(event.target.name==='fname')
            {
                return {
                    fname:event.target.value,
                    lname:preVal.lname
                }
                
            }
            else if(event.target.name==='lname')
            {
                return {
                    lname:event.target.value,
                    fname:preVal.fname
                }
            }
        })
        
        }
       
    

function onSubmit(event){
    event.preventDefault();

}

    return(
        <>
        <div className="Main">
            <form onSubmit={onSubmit}>
                <div>
                  <h3>{fullname.fname} {fullname.lname}</h3>
                    <input type="text" placeholder="Enter Your Name" name="fname" value={fullname.fname} onChange={Change}/>
                    <br></br><input type="text" placeholder="Enter Your Last Name" name="lname" value={fullname.lname}  onChange={Change}/>
                    <br></br><button > Submit </button>
                </div>
         </form>
        </div>
        </>
    )
}

export default Form;
